package Day2;

public class Control {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//take varible for comparision
		int a =1;
		//add condition
		while (a<2) {
			//if condition true, execute next line
			System.out.println("while condition is true");
		a++;
			}
		
		//same using for lop 
	
		for (int i = 0; i < 1; i++) {
			System.out.println("for condition is true");
			
		}
		
		do {
			System.out.println("do is executed");
			
		} while (a<2);

	}

}
